$langClarContent = "<p><b>&#1042;&#1080;&#1082;&#1083;&#1072;&#1076;&#1072;&#1095;</b></p> <p>Dokeos - &#1094;&#1077; &#1089;&#1080;&#1089;&#1090;&#1077;&#1084;&#1072; &#1079;&#1085;&#1072;&#1085;&#1100; &#1090;&#1072; &#1082;&#1077;&#1088;&#1091;&#1074;&#1072;&#1085;&#1085;&#1103; &#1085;&#1072;&#1074;&#1095;&#1072;&#1085;&#1085;&#1103;&#1084;. &#1042;&#1086;&#1085;&#1072; &#1076;&#1086;&#1079;&#1074;&#1086;&#1083;&#1103;&#1108; &#1074;&#1080;&#1082;&#1083;&#1072;&#1076;&#1072;&#1095;&#1091; &#1086;&#1088;&#1075;&#1072;&#1085;&#1110;&#1079;&#1091;&#1074;&#1072;&#1090;&#1080; &#1085;&#1072;&#1074;&#1095;&#1072;&#1083;&#1100;&#1085;&#1110; &#1084;&#1072;&#1090;&#1077;&#1088;&#1110;&#1072;&#1083;&#1080;, &#1087;&#1083;&#1072;&#1085;&#1080; &#1090;&#1072; &#1082;&#1077;&#1088;&#1091;&#1074;&#1072;&#1090;&#1080; &#1074;&#1079;&#1072;&#1108;&#1084;&#1086;&#1076;&#1110;&#1108;&#1102; &#1079;&#1110; &#1089;&#1090;&#1091;&#1076;&#1077;&#1085;&#1090;&#1072;&#1084;&#1080;. &#1042;&#1089;&#1077; &#1094;&#1077; &#1074;&#1080;&#1082;&#1086;&#1085;&#1091;&#1108;&#1090;&#1100;&#1089;&#1103; &#1095;&#1077;&#1088;&#1077;&#1079; &#1074;&#1077;&#1073;-&#1073;&#1088;&#1072;&#1091;&#1079;&#1077;&#1088;.</p> <p>&#1044;&#1083;&#1103; &#1082;&#1086;&#1088;&#1080;&#1089;&#1090;&#1091;&#1074;&#1072;&#1085;&#1085;&#1103; Dokeos &#1074; &#1088;&#1086;&#1083;&#1110; &#1074;&#1080;&#1082;&#1083;&#1072;&#1076;&#1072;&#1095;&#1072; &#1074;&#1072;&#1084; &#1085;&#1077;&#1086;&#1073;&#1093;&#1076;&#1085;&#1110; &#1083;&#1086;&#1075;&#1110;&#1085; &#1090;&#1072; &#1087;&#1072;&#1088;&#1086;&#1083;&#1100;. &#1042; &#1079;&#1072;&#1083;&#1077;&#1078;&#1085;&#1086;&#1089;&#1090;&#1110; &#1074;&#1110;&#1076; &#1085;&#1072;&#1083;&#1072;&#1096;&#1090;&#1091;&#1074;&#1072;&#1085;&#1100; &#1073;&#1077;&#1079;&#1087;&#1077;&#1082;&#1080; &#1074;&#1072;&#1096;&#1086;&#1075;&#1086; &#1087;&#1086;&#1088;&#1090;&#1072;&#1083;&#1091; &#1074;&#1080; &#1084;&#1086;&#1078;&#1077;&#1090;&#1077; &#1084;&#1072;&#1090;&#1080; &#1084;&#1086;&#1078;&#1083;&#1080;&#1074;&#1110;&#1089;&#1090;&#1100; &#1089;&#1072;&#1084;&#1086;&#1088;&#1077;&#1108;&#1089;&#1090;&#1088;&#1072;&#1094;&#1110;&#1111; (&#1084;&#1077;&#1085;&#1102; &#1079;&#1087;&#1088;&#1072;&#1074;&#1072;) &#1072;&#1073;&#1086; &#1088;&#1077;&#1108;&#1089;&#1090;&#1088;&#1072;&#1094;&#1110;&#1111; &#1079; &#1094;&#1077;&#1085;&#1090;&#1088;&#1091; &#1072;&#1076;&#1084;&#1110;&#1085;&#1110;&#1089;&#1090;&#1088;&#1072;&#1094;&#1110;&#1111; &#1074;&#1072;&#1096;&#1086;&#1111; &#1086;&#1088;&#1075;&#1072;&#1085;&#1110;&#1079;&#1072;&#1094;&#1110;&#1111;. &#1047;&#1074;&#1072;&#1078;&#1090;&#1077; &#1085;&#1072; &#1090;&#1077;, &#1097;&#1086; &#1083;&#1086;&#1075;&#1110;&#1085; &#1090;&#1072; &#1087;&#1072;&#1088;&#1086;&#1083;&#1100; &#1085;&#1077; &#1084;&#1086;&#1078;&#1091;&#1090;&#1100; &#1084;&#1072;&#1090;&#1080; &#1087;&#1088;&#1086;&#1073;&#1110;&#1083;&#1110;&#1074;, &#1072; &#1090;&#1072;&#1082;&#1086;&#1078; &#1088;&#1086;&#1079;&#1088;&#1110;&#1079;&#1085;&#1103;&#1102;&#1090;&#1100;&#1089;&#1103; &#1074;&#1077;&#1083;&#1080;&#1082;&#1080; &#1090;&#1072; &#1084;&#1072;&#1083;&#1110; &#1079;&#1085;&#1072;&#1082;&#1080;. &#1042;&#1074;&#1077;&#1076;&#1110;&#1090;&#1100; &#1074;&#1072;&#1096; &#1083;&#1086;&#1075;&#1110;&#1085;/&#1087;&#1072;&#1088;&#1086;&#1083;&#1100;, &#1087;&#1086;&#1090;&#1110;&#1084; &#1089;&#1090;&#1074;&#1086;&#1088;&#1110;&#1090;&#1100; &#1082;&#1091;&#1088;&#1089; (&#1079;&#1074;&#1077;&#1088;&#1093;&#1091; &#1079;&#1087;&#1088;&#1072;&#1074;&#1072;) &#1072;&#1073;&#1086; &#1074;&#1080;&#1082;&#1086;&#1088;&#1080;&#1089;&#1090;&#1086;&#1074;&#1091;&#1081;&#1090;&#1077; &#1082;&#1091;&#1088;&#1089;, &#1097;&#1086; &#1073;&#1091;&#1074; &#1089;&#1090;&#1074;&#1086;&#1088;&#1077;&#1085;&#1080;&#1081; &#1076;&#1083;&#1103; &#1074;&#1072;&#1089; &#1074; &#1094;&#1077;&#1085;&#1090;&#1088;&#1086;&#1084; &#1072;&#1076;&#1084;&#1110;&#1085;&#1110;&#1089;&#1090;&#1088;&#1072;&#1094;&#1110;&#1111; &#1074;&#1072;&#1096;&#1086;&#1111; &#1086;&#1088;&#1075;&#1072;&#1085;&#1110;&#1079;&#1072;&#1094;&#1110;&#1111;. </p> <p> &#1044;&#1083;&#1103; &#1090;&#1086;&#1075;&#1086;, &#1072;&#1073;&#1080; &#1076;&#1086;&#1076;&#1072;&#1090;&#1080; &#1082;&#1086;&#1083;&#1077;&#1075;&#1091; &#1074; &#1088;&#1086;&#1083;&#1110; &#1082;&#1086;-&#1072;&#1076;&#1084;&#1110;&#1085;&#1110;&#1089;&#1090;&#1088;&#1072;&#1090;&#1086;&#1088;&#1072; &#1074;&#1072;&#1096;&#1086;&#1075;&#1086; &#1082;&#1091;&#1088;&#1089;&#1091;: <ol><li> &#1071;&#1082;&#1097;&#1086; &#1074;&#1110;&#1085; &#1097;&#1077; &#1085;&#1077; &#1079;&#1072;&#1088;&#1077;&#1108;&#1089;&#1090;&#1088;&#1086;&#1074;&#1072;&#1085;&#1080;&#1081; &#1074; &#1089;&#1080;&#1089;&#1090;&#1077;&#1084;&#1110;, &#1079;&#1072;&#1088;&#1077;&#1108;&#1089;&#1090;&#1088;&#1091;&#1081;&#1090;&#1077; &#1081;&#1086;&#1075;&#1086; &#1090;&#1072; &#1074;&#1080;&#1073;&#1077;&#1088;&#1110;&#1090;&#1100; «&#1042;&#1080;&#1082;&#1083;&#1072;&#1076;&#1072;&#1095;» &#1103;&#1082; &#1087;&#1088;&#1086;&#1092;&#1110;&#1083;&#1100;,</li> <li> &#1071;&#1082;&#1097;&#1086; &#1074;&#1110;&#1085; &#1074;&#1078;&#1077; &#1079;&#1072;&#1088;&#1077;&#1108;&#1089;&#1090;&#1088;&#1086;&#1074;&#1072;&#1085;&#1080;&#1081; &#1074; &#1089;&#1080;&#1089;&#1090;&#1077;&#1084;&#1110;, &#1074;&#1110;&#1076;&#1082;&#1088;&#1080;&#1081;&#1090;&#1077; &#1079;&#1072;&#1088;&#1072;&#1093;&#1091;&#1074;&#1072;&#1085;&#1085;&#1103; &#1085;&#1072; &#1082;&#1091;&#1088;&#1089; &#1074; &#1085;&#1072;&#1083;&#1072;&#1096;&#1090;&#1091;&#1074;&#1072;&#1085;&#1085;&#1103;&#1093; &#1082;&#1091;&#1088;&#1089;&#1091; &#1090;&#1072; &#1079;&#1072;&#1088;&#1072;&#1093;&#1091;&#1081;&#1090;&#1077; &#1081;&#1086;&#1075;&#1086; &#1085;&#1072; &#1074;&#1072;&#1096; &#1082;&#1091;&#1088;&#1089;. &#1055;&#1086;&#1090;&#1110;&#1084; &#1074;&#1110;&#1076;&#1088;&#1077;&#1076;&#1072;&#1075;&#1091;&#1081;&#1090;&#1077; &#1081;&#1086;&#1075;&#1086; &#1087;&#1088;&#1086;&#1092;&#1110;&#1083;&#1100;, &#1085;&#1072;&#1076;&#1072;&#1074; &#1081;&#1086;&#1084;&#1091; &#1087;&#1088;&#1072;&#1074;&#1072; &#1090;&#1080;&#1087;&#1091; «&#1042;&#1080;&#1082;&#1083;&#1072;&#1076;&#1072;&#1095;».</li></ol></p> <p> &#1050;&#1086;&#1078;&#1077;&#1085; &#1110;&#1085;&#1089;&#1090;&#1088;&#1091;&#1084;&#1077;&#1085;&#1090; Dokeos &#1084;&#1072;&#1108; &#1082;&#1086;&#1085;&#1090;&#1077;&#1082;&#1089;&#1090;&#1085;&#1091; &#1089;&#1087;&#1088;&#1072;&#1074;&#1082;&#1091;, &#1087;&#1088;&#1077;&#1076;&#1089;&#1090;&#1072;&#1074;&#1083;&#1077;&#1085;&#1091; &#1095;&#1077;&#1088;&#1074;&#1086;&#1085;&#1080;&#1084; &#1073;&#1091;&#1108;&#1084;. &#1071;&#1082;&#1097;&#1086; &#1074;&#1080; &#1085;&#1077; &#1079;&#1085;&#1072;&#1081;&#1096;&#1083;&#1080; &#1087;&#1086;&#1090;&#1088;&#1110;&#1073;&#1085;&#1091; &#1110;&#1085;&#1092;&#1086;&#1088;&#1084;&#1072;&#1094;&#1110;&#1102;, &#1087;&#1077;&#1088;&#1077;&#1081;&#1076;&#1110;&#1090;&#1100; &#1085;&#1072; &#1089;&#1090;&#1086;&#1088;&#1110;&#1085;&#1082;&#1091; &#1076;&#1086;&#1087;&#1086;&#1084;&#1086;&#1075;&#1080; Dokeos: <a href=\\\"http://www.dokeos.com/documentation.php\\\">http://www.dokeos.com/documentation.php</a> &#1090;&#1072; &#1089;&#1082;&#1072;&#1095;&#1072;&#1081;&#1090;&#1077; &#1084;&#1072;&#1085;&#1091;&#1072;&#1083; &#1076;&#1083;&#1103; &#1074;&#1080;&#1082;&#1083;&#1072;&#1076;&#1072;&#1095;&#1072; &#1074;&#1072;&#1096;&#1086;&#1102; &#1084;&#1086;&#1074;&#1086;&#1102; &#1072;&#1073;&#1086; &#1072;&#1085;&#1075;&#1083;&#1110;&#1081;&#1089;&#1100;&#1082;&#1086;&#1102;. </p> <p><b>&#1057;&#1090;&#1091;&#1076;&#1077;&#1085;&#1090;</b></p> <p>&#1062;&#1077;&#1081; &#1087;&#1086;&#1088;&#1090;&#1072;&#1083; &#1076;&#1086;&#1079;&#1074;&#1086;&#1083;&#1103;&#1108; &#1074;&#1072;&#1084; &#1073;&#1088;&#1072;&#1090;&#1080; &#1091;&#1095;&#1072;&#1089;&#1090;&#1100; &#1074; &#1082;&#1091;&#1088;&#1089;&#1072;&#1093;, &#1090;&#1088;&#1077;&#1085;&#1110;&#1085;&#1075;&#1072;&#1093;, &#1089;&#1087;&#1110;&#107